CREATE TABLE public.User (id SERIAL NOT NULL PRIMARY KEY,username VARCHAR(100) NOT NULL UNIQUE,password VARCHAR(100) NOT NULL, displayname VARCHAR(100) NOT NULL);
CREATE TABLE public.Job (id SERIAL NOT NULL PRIMARY KEY, category VARCHAR(100) NOT NULL, description TEXT NOT NULL, image VARCHAR(100) NOT NULL);
CREATE TABLE public.Review (id SERIAL NOT NULL PRIMARY KEY,user_id INT NOT NULL REFERENCES public.user(id),job_id INT NOT NULL REFERENCES public.job(id), text TEXT NOT NULL);